const ArithmeticOperationsClass=require("./ArithmeticOperations");
 var obj={
     operation(opr){
var calcOperations=new ArithmeticOperationsClass();
 if(opr=='+'){
 return calcOperations.add(100,200);
 }
 else if(opr=='-'){
     return calcOperations.subtract(200,100);
 }
 else if(opr=='*'){
     return calcOperations.multiply(10,20);
 }
 else{
     return calcOperations.divide(200,10);
 }
     }
 }
 module.exports=obj;